public interface Store
        int ID{ get; set; } // PK, Unique
        int ZipCode{ get; set; }

public interface Customer
        int ID{ get; set; } // PK, Unique
        string FirstName{ get; set; }
        string LastName{ get; set; }
        string Address{ get; set; }
        string AddressAdditional{ get; set; }

public interface Customer
        int ID{ get; set; } // PK, Unique
